import React, { useState, useEffect } from 'react'
import axios from "axios";
import logo from './logo.svg';
import './App.css';
import VideoPlayer from './VideoPlayer'

function App() {

  const [videoNames, setVideoNames] = useState(null);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  function showVideo(vidName){
    setSelectedVideo(vidName);
  }


  useEffect(() => {
    axios({
      method: "GET",
      url: "/getall",
    })
    .then((response) => {
      const res = response.data;
      setVideoNames({
        videos: res.videos.reverse()
      });
      res.videos.forEach(vid => {
        console.log(vid);
      });
    })
    .catch((error) => {
      if (error.response) {
        console.log(error.response);
        console.log(error.response.status);
        console.log(error.response.headers);
      }
    });
  }, []);


  return (




    <div className="App">
      <div className = 'videoButtons'>
      {videoNames && videoNames.videos.map((videoName, index) => {
          const time = videoName.split(/[-.]/);
          const day = parts[1];
          const month = parts[0];
          const year = parts[2];
          const hour = parts[3];
          const min = parts[4];
          const second = parts[5];
          const formattedTime = `${hour}:${min}:${second}`;

          return (
            <button
              key={index}
              className="button"
              onClick={() => showVideo(videoName)}
            >
              Motion Detected
              <br />
              {`${time[0]}/${time[1]}/${time[2]}`}
              <br />
              {formattedTime}
            </button>
          );
        })}
      </div>
      {selectedVideo && (
        <div className = 'displayedVideo'>
          <VideoPlayer videoUrl={selectedVideo} />
        </div>
      )}
    </div>
  );
}




export default App;
